<template>
  <div class="d-inline">
    <img class="perf-img m-2" :class="{'over-date': overDate}" width="170px" src="data:image/jpeg;base64," alt="performance_poster">
  <button class="btn btn-primary" data-toggle="modal" :data-target="'#performance-'+performance.id">modal</button>
  <PerformanceDetail :performanceId="performance.id"/> 
  </div>
</template>

<script>
import PerformanceDetail from './performances/PerformanceDetail'
export default {
  name: 'ClubPerformanceItem',
  props: {
    performance: Object,
    isMaster: Boolean,
  },
  components:{
    PerformanceDetail
  },
  data(){
    return {
      overDate: null,
    }
  },
  methods: {
    dateCheck(){
      let today = new Date()
      let end_date = new Date(this.performance.end_date)
      if (today > end_date){
        this.overDate = true
      } else{
        this.overDate = false
      }
    }
  },
  mounted(){
    this.dateCheck()
  },
}
</script>

<style>
  .perf-img {
    cursor: pointer;
  }
  .perf-img:hover {
    filter: brightness(90%)
  }
  .over-date {
    filter: brightness(50%)
  }
  .over-date:hover {
    filter: brightness(40%)
  }
</style>